\begin{eqnarray*}
		& & f''(x)\cdot f(x)-2[f'(x)]^2+xf^3(x)=0\Leftrightarrow f''(x)\cdot f^2(x)-2[f'(x)]^2 f(x)=-xf^4(x)\\
		&\Leftrightarrow & \dfrac{f''(x)\cdot f^2(x)-2[f'(x)]^2\cdot f(x)}{f^4(x)}=-x\Leftrightarrow \left[ \dfrac{f'(x)}{f^2(x)}\right]'=-x\\
		&\Leftrightarrow &  \displaystyle\int\limits  \left[ \dfrac{f'(x)}{f^2(x)}\right]'\mathrm{\,d}x = \displaystyle\int\limits (-x)\mathrm{\,d}x \Leftrightarrow \dfrac{f'(x)}{f^2(x)}=-\dfrac{x^2}{2}+C.
	\end{eqnarray*}

Với $f'(0)=0$; $f(0)=1$ suy ra $C=0$ và
$$\displaystyle\int\limits_0^1 \dfrac{f'(x)}{f^2(x)} \mathrm{\,d}x=
\displaystyle\int\limits_0^1 \left(-\dfrac{x^2}{2} \right)  \mathrm{\,d}x
\Leftrightarrow \displaystyle\int\limits_0^1 \dfrac{\mathrm{\,d}(f(x))}{f^2(x)} 
=-\dfrac{x^3}{6}\bigg|_0^1
\Leftrightarrow -\dfrac{1}{f(x)}\bigg|_0^1=-\dfrac{1}{6}
\Leftrightarrow \dfrac{1}{f(0)}-\dfrac{1}{f(1)}=-\dfrac{1}{6}
$$
Suy ra $1-\dfrac{1}{f(1)}=-\dfrac{1}{6}\Leftrightarrow \dfrac{1}{f(1)}=\dfrac{7}{6}\Leftrightarrow f(1)=\dfrac{6}{7}.$

Từ giả thiết $$f^3(x)+f(x)=x.$$ Cho $x=0$, suy ra $$f^3(0)+f(0)=0\Rightarrow f(0)=0.$$ Cho $x=2$, suy   ra $$f^3(2)+f(2)=2\Rightarrow f(2)=1.$$ Ta có 
		\begin{eqnarray*}
		& &\displaystyle\int\limits_0^2 \left[\left(f^3(x)+f(x)\right)f'(x)\right]\mathrm{\,d}x=\displaystyle\int\limits_0^2 xf'(x)\mathrm{\,d}x\\ &\Leftrightarrow &\left.\left(\dfrac{f^4(x)}{4}+\dfrac{f^3(x)}{3}\right)\right|_0^2=\left. xf(x)\right|_0^2-\displaystyle\int\limits_0^2 f(x)\mathrm{\,d}x.
		\end{eqnarray*}		
		Từ đây, dễ dàng có được $I=\dfrac{5}{4}.$

Ta có
\begin{align*}
f(x)&=a\ln\left( x+\sqrt{x^2+1} \right)+bx^{2017}+2018\\
&=a\ln \dfrac{1}{\sqrt{x^2+1}-x}+bx^{2017}+2018\\
&=-a\ln \left( \sqrt{x^2+1}-x \right)+bx^{2017}+2018\\
&=-a\ln \left( \sqrt{(-x)^2+1}+(-x) \right)-b(-x)^{2017}+2018\\
&=4036-f(-x),
\end{align*}
mà $\log(\ln 10)=\log\dfrac{1}{\log \mathrm{e}}=-\log(\log\mathrm{e})$ nên
\begin{align*}
f\left( \log\left( \ln 10 \right) \right)=4036-f\left( \log\left( \log \mathrm{e} \right) \right)=4036-2019=2017.
\end{align*}

Từ giả thiết $f'(x)+2xf(x)=2x\mathrm{e}^{-x^2}$, ta suy ra \begin{eqnarray*}
	\mathrm{e}^{x^2}f'(x)+2x\mathrm{e}^{x^2}f(x)=2x&\Leftrightarrow &\left[\mathrm{e}^{x^2}f(x)\right]'=2x\\&\Leftrightarrow & \displaystyle\int\limits_0^1\left[\mathrm{e}^{x^2}f(x)\right]' \mathrm{\,d}x=\displaystyle\int\limits_0^1 2x\mathrm{\,d}x\\ &\Leftrightarrow &\left. \mathrm{e}^{x^2}f(x)\right|_0^1=\left. x^2\right|_0^1\\&\Leftrightarrow &\mathrm{e}f(1)-f(0)=1\Rightarrow f(1)=\dfrac{2}{\mathrm{e}}.
	\end{eqnarray*}
