			Gọi $ I=CH \cap DK $.\\
			Trong $ (SHC) $: Kẻ $ HJ \perp SI $ ($ J\in SI $).\\
			Vì $\heva{
				& DK\perp CH \\ 
				& DK\perp SH}\Rightarrow DK \perp (SHC)\Rightarrow DK \perp HJ$.
		}
		{
			\begin{tikzpicture}[scale=.8, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\tkzDefPoints{0/0/A, 3/-2/C, -2/-2/D}
			\clip(-2.5,-3) rectangle (6,5.5);
			\tkzDefPointBy[translation=from D to C](A) \tkzGetPoint{B}
			\tkzDefMidPoint(A,D) \tkzGetPoint{H}
			\coordinate (S) at ($(H)+(0,5)$);
			\tkzDefMidPoint(C,B) \tkzGetPoint{K}
			\tkzDefMidPoint(H,C) \tkzGetPoint{I}
			\coordinate (J) at ($(S)!2/5!(I)$);
			\tkzDrawSegments(S,D S,C S,B D,C C,B S,K)
			\tkzDrawSegments[dashed](S,A A,D S,H A,B D,K H,J H,B S,I H,C H,K)
			\tkzDrawPoints[fill=black](S,A,B,C,D,H,K,I,J)
			\tkzLabelPoints[left](A)
			\tkzLabelPoints[above](S)
			\tkzLabelPoints[below](H,D,C,I)
			\tkzLabelPoints[right](B,K,J)
			\tkzMarkRightAngles(S,H,B  A,D,C D,A,B D,C,B A,B,C H,J,I C,I,K)
			\tkzLabelSegment[above,pos=0.4](A,B){ $a$}
			\tkzLabelSegment[left,pos=.5](A,D){ $2a$}
			\tkzMarkSegments[mark=||](H,A H,D)
