			\tkzInterLL(H,c)(A,C)    \tkzGetPoint{P}
			\tkzDefLine[parallel=through H](A,C) \tkzGetPoint{d}
			\tkzInterLL(H,d)(B,C)    \tkzGetPoint{Q}
			\tkzDrawSegments[dashed](A,C H,P H,Q S,P)
			\tkzDrawSegments(S,H S,B S,Q)
			\tkzLabelPoints[above](S)
			\tkzLabelPoints[below](B) 
			\tkzLabelPoints[below right](Q) 
			\tkzLabelPoints[above right](P) 
			\tkzLabelPoints[below left](H) 
			\tkzLabelPoints[right](C)
			\tkzLabelPoints[left](A)
