	Có bao nhiêu cặp số nguyên $\left(x,y\right)$ thỏa mãn ${\log_{\sqrt{3}}}\dfrac{x+y}{x^2+y^2+xy+2}=x\left(x-3\right)+y\left(y-3\right)+xy.$
	\choice
	{$1$}
	{$2$}
	{$4$}
	{\True $6$}
	\loigiai{
		\allowdisplaybreaks
		\begin{eqnarray*}
			&&{\log_{\sqrt{3}}}\dfrac{x+y}{x^2+y^2+xy+2}=x\left(x-3\right)+y\left(y-3\right)+xy\\
			&\Leftrightarrow& {\log_{\sqrt{3}}}\dfrac{3x+3y}{x^2+y^2+xy+2}=\left(x^2+y^2+xy+2\right)-\left(3x+3y\right)\\
			&\Leftrightarrow& {\log_{\sqrt{3}}}\left(3x+3y\right)+\left(3x+3y\right)={\log_{\sqrt{3}}}\left(x^2+y^2+xy+2\right)+\left(x^2+y^2+xy+2\right).\quad (1)
		\end{eqnarray*}
		Ta thấy hàm số $f\left(t\right)={\log_{\sqrt{3}}}t+t$ đồng biến trên $\left(0;+\infty\right)$, do đó:
		\allowdisplaybreaks
		\begin{eqnarray*}
			\left(1\right)&\Leftrightarrow& f\left(3x+3y\right)=f\left(x^2+y^2+xy+2\right)\\
			&\Leftrightarrow& 3x+3y=x^2+y^2+xy+2\\
			&\Leftrightarrow& 3\left(x+y\right)=\left(x+y\right)^2-xy+2.\quad (2)
		\end{eqnarray*}
		Đặt $\heva{& x+y=S\\& xy=P}$, (2) thành $3S=S^2-P+2\Leftrightarrow P=S^2-3S+2$.\\
		Để tồn tại cặp số nguyên $\left(x,y\right)$ thỏa mãn giả thiết, điều kiện cần là 
		\allowdisplaybreaks
		\begin{eqnarray*}  
			&&S^2-4P\ge 0\\
			&\Leftrightarrow& S^2-4\left(S^2-3\text{S}+2\right)\ge 0\\
			&\Leftrightarrow& -3S^2+12\text{S}-8\ge 0\\
			&\Leftrightarrow& \dfrac{6-2\sqrt{3}}{3}\le S\le \dfrac{6+2\sqrt{3}}{3}.
		\end{eqnarray*}  
		Vì $\left(x,\,y\right)$ là cặp số nguyên khi $S$ nguyên, ta chọn $S\in \left\{1\ ,\ 2\ ,\ 3 \right\}$.
		\begin{itemize}
			\item Với $S=1\Leftrightarrow P=0$ ta được $\hoac{
				& \left(x\ ,\ y\right)=\left(0\ ,\ 1\right) \\
				& \left(x\ ,\ y\right)=\left(1\ ,\ 0\right).}$ 
			\item Với $S=2\Leftrightarrow P=0$ ta được $\hoac{
				& \left(x\ ,\ y\right)=\left(0\ ,\ 2\right) \\
				& \left(x\ ,\ y\right)=\left(2\ ,\ 0\right).}$ 
			\item Với $S=3\Leftrightarrow P=2$ ta được $\hoac{
				& \left(x\ ,\ y\right)=\left(2\ ,\ 1\right) \\
				& \left(x\ ,\ y\right)=\left(1\ ,\ 2\right).}$ 
		\end{itemize}
		Vậy có $6$ cặp số nguyên $\left(x,y\right)$ thỏa mãn yêu cầu bài toán.\\
		%------------------------------------------------
		\textbf{Cách 2:}\\
		\allowdisplaybreaks
		\begin{eqnarray*}
			&&{\log_{\sqrt{3}}}\dfrac{x+y}{x^2+y^2+xy+2}=x\left(x-3\right)+y\left(y-3\right)+xy\\
			&\Leftrightarrow& {\log_{\sqrt{3}}}\dfrac{3x+3y}{x^2+y^2+xy+2}=\left(x^2+y^2+xy+2\right)-\left(3x+3y\right)\\
			&\Leftrightarrow& {\log_{\sqrt{3}}}\left(3x+3y\right)+\left(3x+3y\right)={\log_{\sqrt{3}}}\left(x^2+y^2+xy+2\right)+\left(x^2+y^2+xy+2\right).\quad (1)
		\end{eqnarray*}
		Ta thấy hàm số $f\left(t\right)={\log_{\sqrt{3}}}t+t$ đồng biến trên $\left(0;+\infty\right)$, do đó
		\allowdisplaybreaks
		\begin{eqnarray*}
			\left(1\right) &\Leftrightarrow& f\left(3x+3y\right)=f\left(x^2+y^2+xy+2\right)\\
			&\Leftrightarrow& 3x+3y=x^2+y^2+xy+2 \\
			&\Leftrightarrow& x^2+\left(y-3\right)x+y^2-3y+2=0.\quad (2)
		\end{eqnarray*}
		Coi (2) là phương trình ẩn $x$ và $y$ là tham số, theo giả thiết (2) phải có
		\allowdisplaybreaks
		\begin{eqnarray*}
			&&\Delta =\left(y-3\right)^2-4\left(y^2-3y+2\right)\ge 0\\
			&\Leftrightarrow& -3y^2+6y+1\ge 0\\
			&\Leftrightarrow& \dfrac{3-2\sqrt{3}}{3}\le y\le \dfrac{3+2\sqrt{3}}{3}.
		\end{eqnarray*}
		\begin{itemize}
			\item	Với $y\in \mathbb{Z}$ ta chọn $y\in \left\{0,\, 1,\, 2 \right\}$. 

			\item	Với $y=0$, (2) thành $x^2-3x+2=0\Leftrightarrow \hoac{& x=1\\& x=2}$. 
			\item	Với $y=1$, (2) thành $x^2-2x=0\Leftrightarrow \hoac{& x=0\\& x=2}$. 
			\item	Với $y=2$, (2) thành $x^2-x=0\Leftrightarrow \hoac{& x=0\\& x=1}$. 
		\end{itemize}
		Vậy có $6$ cặp số nguyên $\left(x,\,y\right)$ thỏa mãn yêu cầu bài toán.}
