	\choice
	{$\dfrac{SB}{SN}=\dfrac{11}{2}$}
	{$\dfrac{SB}{SN}=5$}
	{\True $\dfrac{SB}{SN}=4$}
	{$\dfrac{SB}{SN}=\dfrac{9}{2}$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[>=stealth, line join=round, line cap = round,scale=0.8]
			\tkzDefPoints{0/0/A, 5/0/D, -2/-2/B, 3/-2/C}
			\coordinate (O) at ($(A)!0.5!(C)$);
			\coordinate (S) at ($(O)+(0,5)$);
