			\coordinate (P) at ($(S)!0.33!(C)$);
			\tkzInterLL(M,P)(S,O) \tkzGetPoint{H};
			\coordinate (N) at ($(S)!0.4!(B)$);
			\tkzInterLL(N,H)(S,D) \tkzGetPoint{Q};
			\tkzDrawSegments(S,B S,C S,D B,C C,D)
			\tkzDrawSegments[dashed](S,A A,B A,D A,C B,D S,O N,Q M,P)
			\tkzLabelPoints[above right](S)
