			\coordinate (H) at ($(O)!0.5!(D)$);
			\tkzDrawPolygon(S,B,C,D)
			\tkzDrawSegments(S,C)
			\tkzDrawSegments[dashed](A,S A,B A,D A,C B,D O,S M,N N,H H,M)
