%[Thi thử lần 3, THPT Đô Lương 4, Nghệ AN, 2020]%[GV Đặng Minh Trường, 12EX11]%[2H1K3-6]
	Cho tam giác $ ABC $ đều cạnh bằng $ 4 $. Trên đường thẳng $ d $ đi qua $ A $ và vuông góc với mặt phẳng $ (ABC) $ lấy điểm $ M $ sao cho $ AM=x $. Gọi $ P $, $ Q $ lần lượt là hình chiếu vuông góc của điểm $ C $ lên $ AB $, $ MB $. Đường thẳng qua $ P $, $ Q $ cắt $ d $ tại $ N $. Thể tích khối tứ diện $ BCMN $ đạt giá trị nhỏ nhất bằng
	\choice
	{$ \dfrac{4\sqrt{6}}{3} $}
	{\True $ \dfrac{16\sqrt{6}}{3} $}
	{$ \dfrac{16\sqrt{3}}{3} $}
	{$ \dfrac{8\sqrt{3}}{3} $}
	\loigiai
	{\immini
		{
			Từ giả thiết ta có 
			\[\heva{&  CP \perp AB \\ & CP \perp MA} \Rightarrow CP \perp MB.\]
			Lại có $ CQ \perp  MB$ nên ta có $ MB \perp (CPQ) \Rightarrow MB \perp NQ $.\\
			Khi đó tứ giác $ AMQP $ nội tiếp nên $ \widehat{AMB}=\widehat{APN} $.\\
			Xét hai tam giác $ MAB $ và $ PAN $ có:\\
			$ \widehat{AMB}=\widehat{APN} $ và $ \widehat{MAB}=\widehat{PAN}=90^{\circ} $ nên đồng dạng.\\
			Suy ra $ \dfrac{MA}{PA}=\dfrac{AB}{AN} \Leftrightarrow MA\cdot NA=PA \cdot AB = 8 $.
		}
		{
			\begin{tikzpicture}[line join = round, line cap = round,>=stealth,font=\footnotesize,scale=1]
			\tkzDefPoints{0/0/A}
			\coordinate (C) at ($(A)+(4,0)$);
			\tkzDefShiftPoint[A](-45:2){B}
			\coordinate (M) at ($(A)+(0,3)$);
			\coordinate (P) at ($(A)!0.5!(B)$);
			\coordinate (Q) at ($(M)!0.75!(B)$);
			\tkzInterLL(P,Q)(A,M)    \tkzGetPoint{N}
			\tkzMarkRightAngles[size=.2](C,P,B C,Q,B)
			\tkzDrawPolygon(M,A,B,C)
			\tkzDrawSegments(M,B C,Q N,Q A,N)
			\tkzDrawSegments[dashed](A,C C,P)
			\tkzDrawPoints[fill=black](A,C,B,M,N,P,Q)
			\tkzLabelPoints[above](M)
			\tkzLabelPoints[below](B)
			\tkzLabelPoints[left](A,P,Q,N)
			\tkzLabelPoints[right](C)
			\tkzLabelLine[pos=0.5,left,blue](M,A){$x$}
			\end{tikzpicture}
		}
		\noindent Ta có:
		\begin{align*}
		V_{MNBC} &= V_{MABC}+V_{NABC} = \dfrac{1}{3} \left(MA+NA\right) \cdot S_{ABC} = \dfrac{4\sqrt{3}}{3} \left(MA+NA \right) \\
		&\ge \dfrac{4\sqrt{3}}{3} \cdot 2 \cdot \sqrt{MA \cdot NA} = \dfrac{16\sqrt{6}}{3}.
		\end{align*}
		Dấu	bằng xảy ra khi $ MA=NA=2\sqrt{2} $.\\
		Vậy thể tích của khối tứ diện $ BCMN $ đạt giá trị nhỏ nhất bằng $ \dfrac{16\sqrt{6}}{3} $.
	}

%[Thi thử lần 3, THPT Đô Lương 4, Nghệ AN, 2020]%[GV Đặng Minh Trường, 12EX11]%[2D2G4-3]
	Biết rằng có một giá trị $ m $ để hàm số $ f(x)=\dfrac{2019^x}{\ln 2019}+\dfrac{2020^x}{\ln 2020}+mx^2-2x $ đồng biến trên $ \mathbb{R} $. Giá trị $ m $ thuộc khoảng nào sau đây?
	\choice
	{$ \left(\dfrac{15}{2};8\right) $}
	{$ \left(-6;-5\right) $}
	{\True $ \left(-8;-7\right) $}
	{$ \left(-10;-9\right) $}
	\loigiai
	{Hàm số $ f(x) $ đồng biến trên $ \mathbb{R} $ khi và chỉ khi $ f'(x)=2019^x+2020^x+2mx-2 \ge 0 $, $ \forall x \in \mathbb{R} $.\\
		Ta có $ f"(x)=2019^x \ln 2019 + 2020^x \ln 2020 +2m $.\\
		Vì $ x=0 $ là một nghiệm của $ f'(x) $, do đó điều kiện cần để $ f'(x) \ge 0 $, $ \forall x \in \mathbb{R} $ là $ x=0 $ là nghiệm bội, tức là $ f"(0) = 0 \Rightarrow m=-\dfrac{\ln \left(2019 \cdot 2020\right)}{2} \approx -7,6 $.\\
		Ngược lại, với $ m= -\dfrac{\ln \left(2019 \cdot 2020\right)}{2}$, xét hàm số 
		\[ g(x)=f'(x)= 2019^x+2020^x-x\ln \left(2019 \cdot 2020\right)-2.\]
		Do $ g'(x)=2019^x \ln 2019 - \ln 2019 +2020^x \ln 2020 -\ln 2020  $ là hàm đồng biến trên $ \mathbb{R} $ nên $ g'(x) $	có không quá một nghiệm.\\
		Lại do $ g'(0)=0 $ nên $ g'(x)=0 $ có nghiệm duy nhất $ x=0 $.\\
		Ta có $ g'(-1)=-\dfrac{2018\ln 2019}{2019}-\dfrac{2019 \ln 2020}{2020}<0 $ và $ g'(1)=2018\ln2019 + 2019\ln2020 >0 $ nên ta có bảng biến thiên của $ g(x) $:
		\begin{center}
			\begin{tikzpicture}
			\tkzTabInit[lgt=1.2,espcl=2.5]
			{$x$/0.8, $g'(x)$/0.8, $g(x)$/2}
			{$-\infty$,$0$,$+\infty$}
			\tkzTabLine{ ,-,0,+, }
			\tkzTabVar{+/$+\infty$,-/$0$,+/$+\infty$}
			\end{tikzpicture}
		\end{center}
		Suy ra $ f'(x)=g(x) \ge 0 $, $ \forall x \in \mathbb{R} $ hay hàm số $ f(x) $ đồng biến trên $ \mathbb{R} $.\\
		Vậy $ m=-\dfrac{\ln \left(2019 \cdot 2020\right)}{2} \approx -7,6 \in \left(-8;-7\right) $ là giá trị cần tìm.
	}
