%[Thi thử, Thpt 2020, Phu Duc - Thai Binh]%[Bùi Anh Tuấn, 12EX-11]%[2D1Y5-3]
	\immini
	{
		Cho hàm số bậc bốn $ y=f(x) $ có đồ thị trong hình vẽ bên dưới. Tập nghiệm của phương trình $ f(x)=1 $ có số phần tử là
		\choice
		{\True $3$}
		{$2$}
		{$1$}
		{$4$}
	}
	{
		\begin{tikzpicture}[scale=0.7, font=\footnotesize, line join=round, line cap=round, >=stealth]
		\def\a{1/4} % Hệ số a phải khác 0
		\def\b{-2}
		\def\c{1}
		\draw[->] (-3.2,0) -- (3.2,0) node[below] {$x$};
		\draw[->] (0,-3.2) -- (0,2.5) node[left] {$y$};
		\draw (0,0)node[below left]{$O$};
		\draw[samples=150,smooth,domain=-2.9:2.9] plot(\x,{\a*(\x)^4+(\b)*(\x)^2+(\c)});
		\draw[dashed] (-2,0) node[above]{$ -2 $}|-(0,-3)node[above left]{$ -3 $}-|(2,0)node[above]{$ 2 $};
		\foreach \x in {-2,0,2} \fill (\x,0) circle (1pt);
		\foreach \y in {1,-3} \fill (0,\y) circle (1pt);
		\draw (0,1)node[above left]{$1$};
		\end{tikzpicture}
	}
	\loigiai{
		\immini
		{
			Số nghiệm của phương trình $ f(x)=1 $ bằng số giao điểm của hai đồ thị $ y=f(x)  \,\, (C)$ và $ y=1  \,\,(d)$.\\
			Dựa vào đồ thị hàm số $ y=f(x) $, ta thấy đường thẳng $ y=1 $ cắt đồ thị tại $ 3 $ điểm phân biệt.
		}
		{
			\begin{tikzpicture}[scale=0.7, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\def\a{1/4} % Hệ số a phải khác 0
			\def\b{-2}
			\def\c{1}
			\draw[<->,blue] (-3.2,1) -- (3.2,1) node[above] {$d$};
			\draw[->] (-3.2,0) -- (3.2,0) node[below] {$x$};
			\draw[->] (0,-3.2) -- (0,2.5) node[left] {$y$};
			\draw (0,0)node[below left]{$O$};
			\draw[samples=150,smooth,domain=-2.9:2.9] plot(\x,{\a*(\x)^4+(\b)*(\x)^2+(\c)});
			\draw[dashed] (-2,0) node[above]{$ -2 $}|-(0,-3)node[above left]{$ -3 $}-|(2,0)node[above]{$ 2 $};
			\foreach \x in {-2,0,2} \fill (\x,0) circle (1pt);
			\foreach \y in {1,-3} \fill (0,\y) circle (1pt);
			\draw (0,1)node[above left]{$1$};
			\end{tikzpicture}
		}
	}

%[Thi thử, Thpt 2020, Phu Duc - Thai Binh]%[Bùi Anh Tuấn, 12EX-11]%[2H2Y2-1]
	Cho mặt cầu có bán kính $R$. Diện tích của mặt cầu đã cho bằng
	\choice
	{\True $4\pi R^2$}
	{$4\pi R$}
	{$\dfrac{4\pi R^2}{3}$}
	{$\dfrac{4\pi R^3}{3}$}
	\loigiai{
		Công thức tính diện tích mặt cầu có bán kính $ R $ là $ S= 4\pi R^2$.
	}

%[Thi thử, Thpt 2020, Phu Duc - Thai Binh]%[Bùi Anh Tuấn, 12EX-11]%[2D1Y1-2]
	\immini
	{
		Cho hàm số $ y=f(x) $ có đồ thị như hình vẽ bên. Hàm số $ y=f(x) $ đồng biến trên khoảng nào sau đây ?
		\choice
		{$(0;4)$}
		{$(-\infty;0)$}
		{\True $(0;2)$}
		{$(0;3)$}
	}
	{
		\begin{tikzpicture}[scale=0.7, font=\footnotesize, line join=round, line cap=round, >=stealth]
		\def\a{-1} % Hệ số a phải khác 0
		\def\b{3}
		\def\c{0}
		\def\d{0}
		\draw[->] (-1.5,0) -- (3.5,0) node[above] {$x$};
		\draw[->] (0,-1.2) -- (0,5) node[left] {$y$};
		\draw (0,0)node[below left]{$O$};
		\draw[samples=150,smooth,domain=-1.1:3.1] plot(\x,{\a*(\x)^3+(\b)*(\x)^2+(\c)*(\x)+(\d)});
		\draw[dashed] (-1,0) node[below]{$ -1 $}|-(0,4)node[above right]{$ 4 $}-|(2,0)node[below]{$ 2 $};
		\foreach \x in {-1,0,1,2,3} 
		\draw[shift={(\x,0)}] (0,1pt)--(0,-1pt);
		\foreach \y in {-1,1,2,3,4} 
		\draw[shift={(0,\y)}] (1pt,0)--(-1pt,0);
		\end{tikzpicture}
	}
	\loigiai{
		Dựa vào đồ thị hàm số $ y=f(x) $, hàm số đồng biến trên $ (0;2) $ và nghịch biến $ (-\infty;0) $, $ (0;+\infty) $.
	}

%[Thi thử, Thpt 2020, Phu Duc - Thai Binh]%[Bùi Anh Tuấn, 12EX-11]%[2D4Y1-1]
	Tìm phần ảo của số phức $ z=19-20i $
	\choice
	{$20i$}
	{\True $-20$}
	{$-20i$}
	{$19$}
	\loigiai{
		Số phức $ z=19-20i $ có phần thực là $ 19 $ và phần ảo là $ -20 $.
	}

%[Thi thử, Thpt 2020, Phu Duc - Thai Binh]%[Bùi Anh Tuấn, 12EX-11]%[2D2B5-1]
	Phương trình $\log_2 x=2$ có nghiệm là
	\choice
	{$x=2$}
	{$x=3$}
	{$x=1$}
	{\True $x=4$}
	\loigiai{
		Ta có	$\log_2 x=2\Leftrightarrow x=2^2=4$.
	}

%[Thi thử,Trường THPT PLeiku, 2020]%[Duy Sơn La]%[2H2B1-2] 
	Cho hình trụ có bán kính đáy $ r=4 $ cm và khoảng cách giữa hai đáy bằng $10$ cm. Diện tích xung quanh của hình trụ đã cho bằng
	\choice
	{$40\pi$ $\mathrm{cm}^2$}
	{$\dfrac{160}{3}\pi$ $\mathrm{cm}^2$}
	{\True $80\pi$ $\mathrm{cm}^2$}
	{$\dfrac{40}{3}\pi$ $\mathrm{cm}^2$}
	\loigiai{
		Độ dài đường sinh là $l=10$ cm.\\
		Diện tích xung quanh của hình trụ là $S_{xq}=2\pi rl=2\pi \cdot 4\cdot 10=80\pi$ $\mathrm{cm}^2$.
	}

%[Thi thử,Trường THPT PLeiku, 2020]%[Duy Sơn La]%[2D1B5-4] 
	Số giao điểm của đồ thị hàm số $y=x^3-3x^2+4$ và đường thẳng $y=-4x+8$ là
	\choice
	{\True $1$}
	{$0$}
	{$2$}
	{$3$}
	\loigiai{
		Hoành độ giao điểm của đồ thị hàm số $y=x^3-3x^2+4$ và đường thẳng $y=-4x+8$ là nghiệm phương trình sau $$x^3-3x^2+4=-4x+8\Leftrightarrow x^3-3x^2+4x-4=0\Leftrightarrow x=2.$$
	}

%[Thi thử,Trường THPT PLeiku, 2020]%[Duy Sơn La]%[2D1Y2-2] 
	Cho hàm số $y=f(x)$ có bảng biến thiên như hình bên.
	\begin{center}
		\begin{tikzpicture}
		\tkzTabInit[nocadre=false,lgt=1.2,espcl=2.5,deltacl=0.6]
		{$x$ /0.6,$y’$ /0.6,$y$ /2}
		{$-\infty$,$-1$,$0$,$1$,$+\infty$};
		\tkzTabLine{,-,0,+,0,-,0,+,};
