	{$\dfrac{a\sqrt{10}}{7}$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=0.6,line join=round, line cap=round,thick]
			\tikzset{label style/.style={font=\footnotesize}}
			\coordinate (A) at (0,0);
			\coordinate (B) at (-2,-3);
			\coordinate (D) at (7,0);
			\coordinate (C) at ($(B)+(D)-(A)$);
			\coordinate (S) at ($(A)+(0,5)$);
			\coordinate (E) at ($(A)+(B)-(C)$);
			\coordinate (M) at ($(B)!0.5!(E)$);
			\coordinate (H) at ($(S)!(A)!(M)$);
			\tkzDrawSegments(S,B S,C S,D B,C C,D S,E B,E S,M)
			\tkzDrawSegments[dashed,thin](A,D A,B S,A A,B A,C A,E A,M A,H)
			\tkzMarkRightAngles[size=0.2,thin](S,A,D A,M,B A,H,M)
			\tkzDrawPoints[fill=black,size=2pt](S,A,B,C,D,E,M,H)
			\tkzLabelPoints[above](S)
			\tkzLabelPoints[below](A,B,D,M)
			\tkzLabelPoints[right](C)
			\tkzLabelPoints[left](E,H)
