\begin{eqnarray*}	
			\displaystyle\int\limits $\dfrac{1}$\sqrt{x}+x \sqrt{x+1}}\mathrm{\,d}x&=&\displaystyle\int\limits $\dfrac{(x+1)\sqrt{x}-x \sqrt{x+1}}$^{2}x-x^{2}(x+1)}\mathrm{\,d}x\\
			&=&\displaystyle\int\limits $\dfrac{(x+1)\sqrt{x}-x \sqrt{x+1}}$)}\mathrm{\,d}x\\&=&\displaystyle\int\limits\left($\dfrac{1}${x}}-$\dfrac{1}${x+1}}\right)\mathrm{\,d}x=2 \sqrt{x}-2 \sqrt{x+1}+C.
		\end{eqnarray*}
		Suy ra $f(x)=2\sqrt{x}-2\sqrt{x+1}+C$ mà $f(1)=2-2\sqrt{2}\Leftrightarrow C=0$.\\
		Ta có
		\begin{eqnarray*}
			\displaystyle\int\limits_{1}^{3}f(x)\mathrm{\,d}x&=&\displaystyle\int\limits_{1}^{3}(2 \sqrt{x}-2 \sqrt{x+1})\mathrm{\,d}x\\&=&\left.\left($\dfrac{4}$sqrt{x}-$\dfrac{4}$1)\sqrt{x+1}\right)\right|_{1}^{3}\\&=&4\left(\sqrt{3}+$\dfrac{2 \sqrt{2}}$right).
		\end{eqnarray*}

