		\fill (\b,0) circle(1pt);
		\node at ($(\b,0)+(-70:3mm)$) {$b$};
		\fill (\c,0) circle(1pt);
		\node at ($(\c,0)+(-120:3mm)$) {$c$};
		\clip (\xmin+0.1,\ymin+0.1) rectangle (\xmax-0.2,\ymax-0.1);
		\draw[smooth,samples=300] plot(\x,{\d*(\x-\a)*(\x-\b)*(\x-\c)/(\a*\b*\c)});
		\end{tikzpicture}}
	\loigiai{
		\immini{
			Gọi $H_1$ là hình phẳng giới hạn bởi đồ thị hàm số $y=f'(x)$, trục $Ox$ và hai đường thẳng $x=a$, $x=b$. Gọi $H_2$ là hình phẳng giới hạn bởi đồ thị hàm số $y=f'(x)$, trục $Ox$ và hai đường thẳng $x=b$, $x=c$. Gọi $S_1$, $S_2$ lần lượt là diện tích của $H_1$, $H_2$. Theo hình vẽ, ta có $S_2>S_1$. Ta có}
		{
			\begin{tikzpicture}[scale=1,>=stealth, font=\footnotesize, line join=round, line cap=round]
			\def \a{-0.9} \def \b{0.4} \def\c{2.6} \def\d{0.7}
			\def\xmin{-2} \def\xmax{3.5}
			\def\ymin{-1.7} \def\ymax{3}
			\draw[->] (\xmin,0)--(\xmax,0) node [below]{$x$};
			\draw[->] (0,\ymin)--(0,\ymax) node [left]{$y$};
			\node at (0,0) [above left]{$O$};
			\fill (\a,0) circle(1pt);
			\node at ($(\a,0)+(-120:3mm)$) {$a$};
			\fill (\b,0) circle(1pt);
			\node at ($(\b,0)+(-70:3mm)$) {$b$};
			\fill (\c,0) circle(1pt);
			\node at ($(\c,0)+(-120:3mm)$) {$c$};
			\clip (\xmin+0.1,\ymin+0.1) rectangle (\xmax-0.2,\ymax-0.1);
			\draw[smooth,samples=300] plot(\x,{\d*(\x-\a)*(\x-\b)*(\x-\c)/(\a*\b*\c)});
			\fill[pattern=north east lines,opacity=0.8]plot[domain=\a:\b](\x,{\d*(\x-\a)*(\x-\b)*(\x-\c)/(\a*\b*\c)})--cycle;\node[fill=white] at (-0.32,-0.3) {$S_1$};
			\fill[pattern=north west lines,opacity=0.8]plot[domain=\b:\c](\x,{\d*(\x-\a)*(\x-\b)*(\x-\c)/(\a*\b*\c)})--cycle;\node[fill=white] at (1.8,1) {$S_2$};
			\end{tikzpicture}
		}
		\begin{eqnarray*}
			0<S_1&=&\displaystyle \int \limits_a^b \left|f'(x)\right|\mathrm{\, d}x\\
			&=&-\displaystyle \int \limits_a^b f'(x) \mathrm{\, d}x\\ &=&-\left[f(b)-f(a)\right]=f(a)-f(b).
		\end{eqnarray*} Suy ra $f(a)>f(b)$. Ta lại có
		\begin{eqnarray*}
			0<S_2-S_1&=&\displaystyle \int \limits_b^c \left|f'(x)\right|\mathrm{\, d}x -\displaystyle \int \limits_a^b \left|f'(x)\right|\mathrm{\, d}x\\
			&=&\displaystyle \int \limits_b^c f'(x) \mathrm{\, d}x+\displaystyle \int \limits_a^b f'(x) \mathrm{\, d}x\\ &=&\displaystyle \int \limits_a^c f'(x) \mathrm{\, d}x=f(c)-f(a).
		\end{eqnarray*}
		Suy ra $f(c)>f(a)$.\\
		Vậy $f(c)>f(a)>f(b)$.
	}

