	Trong không gian với hệ tọa độ $Oxyz$, cho ba điểm $A(2;2;0),B(4;2;-1),C(3;2;2)$ và mặt phẳng $(P):3x-3y-2z-12=0$. Gọi điểm $M(a;b;c)$ thuộc $(P)$ sao cho $MA^2+MB^2+3MC^2$ đạt giá trị nhỏ nhất. Tính tổng $a-b+c$.
	\choice
	{$0$}
	{$-2$}
	{$1$}
	{\True $2$}
	\loigiai{
		Đặt $\log_3 x=a$, $\log_3 y=b$, $\log_3 z=c$.\\
		Suy ra $\log_3 (xyz)=a+b+c$ và $\log_{xyz} 3=\dfrac{1}{a+b+c}$.\\
		Theo giả thiết 
		\begin{eqnarray*}
			&&\log_3^2\left(\dfrac{x}{9}\right)+\log_3^2\left(\dfrac{y}{9}\right)+\log_3^2\left(\dfrac{z}{9}\right)=2\left[\mathrm{e}^{\log_3(xyz)}-\mathrm{e}^2\cdot \left(\sqrt[4]{\mathrm{e}}^{\log_3^2x+\log_3^2y+\log_3^2z}\right)+2\right]\\
			&\Rightarrow& (a-2)^2+(b-2)^2+(c-2)^2=2\left(\mathrm{e}^{a+b+c}-\mathrm{e}^{2+\tfrac{a^2+b^2+c^2}{4}}+2\right)\\
			&\Leftrightarrow& \mathrm{e}^{\tfrac{a^2+b^2+c^2+8}{4}}+4\cdot \left(\dfrac{a^2+b^2+c^2+8}{4}\right)=2\mathrm{e}^{a+b+c}+4(a+b+c).\quad (1)
		\end{eqnarray*}
		Xét $f(t)=\mathrm{e}^t+4t\Rightarrow f'(t)=\mathrm{e}^t+4>0$, $\forall t\in \Bbb{R}$.\\
		Suy ra hàm số đồng biến trên $\Bbb{R}$.\\
		Khi đó
		\begin{eqnarray*}
			(1)&\Leftrightarrow& f\left(\dfrac{a^2+b^2+c^2+8}{4}\right)=f(a+b+c)\\
			&\Leftrightarrow& \dfrac{a^2+b^2+c^2+8}{4}=a+b+c\\
			&\Leftrightarrow& a^2+b^2+c^2+8=4(a+b+c)\\
			&\Leftrightarrow& (a-2)^2+(b-2)^2+(c-2)^2=4.\quad (2)
		\end{eqnarray*}

		Ta có $P=\dfrac{a(a-1)+b(b-2)+c(c-3)+8}{a+b+c}=\dfrac{3a+2b+c}{a+b+c}$\\
		$\Rightarrow P(a+b+c)=3a+2b+c\Leftrightarrow (P-3)a+(P-2)b+(P-1)c=0.\quad (3)$\\
		Xét mặt cầu $(S)$ có tâm $I(2;2;2)$, bán kính $R=2$ và mặt phẳng 
		$$(\alpha)\colon (P-3)x+(P-2)y+(P-1)z=0.$$
		Từ $(2)$, $(3)$ suy ra để tồn tại $P$ thì $\mathrm{d}(I;(\alpha))\le R$
		\begin{eqnarray*}
			&\Leftrightarrow& \dfrac{|2(P-3)+2(P-2)+2(P-1)|}{\sqrt{(P-3)^2+(P-2)^2+(P-1)^2}}\le 2\\
			&\Leftrightarrow& \dfrac{|6P-12|}{\sqrt{3P^2-12P+14}}\le 2\\
			&\Leftrightarrow& (3P-6)^2\le 3P^2-12P+14\\
			&\Leftrightarrow& 3P^2-24P+22\le 0\\
			&\Leftrightarrow& P\in \left[\dfrac{6-\sqrt{3}}{3};\dfrac{6+\sqrt{3}}{3}\right].
		\end{eqnarray*}
		Vì $P$ nguyên nên $P=1$.\\
		Vậy tập hợp $S$ có một phần tử.
	}

%[Thi thử, THPT Nguyễn Huệ, Bà Rịa Vũng Tàu, 2020]%[Hùng Nam Lý, 12EX11]%[2D2G5-5]
	Xét các số thực không âm $x$ và $y$ thỏa mãn $x+y.2^{x+y}\geq1$. Giá trị nhỏ nhất của biểu thức $P=x^2+y^2+x+2y$ bằng
	\choice
	{$\dfrac{21}{4}$}
	{\True $\dfrac{15}{8}$}
	{$2$}
	{$2$}
	\loigiai{
		$\forall a, b\in \left(\dfrac{1}{\sqrt{6}};1\right)$, ta có
		\begin{eqnarray*}
			&&3\left(a^2+b^2\right)^2+\log_2\dfrac{a^2+b^2}{\left(1-a^2\right)\left(1-b^2\right)}\ge \log_2\left[3m\left(3a^2+3b^2-1\right)\right]+4a^2+4b^2-\dfrac{4}{3}\\
			&\Leftrightarrow&3\left(a^2+b^2\right)^2-4\left(a^2+b^2\right)+\dfrac{4}{3}+\log_2\dfrac{a^2+b^2}{\left(1-a^2\right)\left(1-b^2\right)}-\log_2\left(3a^2+3b^2-1\right)
			\ge \log_2(3m).
		\end{eqnarray*}
		Suy ra $(1)$ đúng với mọi $a$, $b\in \left(\dfrac{1}{\sqrt{6}};1\right)\Leftrightarrow \min P\ge \log_3(3m)$ trong đó\\ $P=3\left(a^2+b^2\right)^2-4\left(a^2+b^2\right)+\dfrac{4}{3}+\log_2\dfrac{a^2+b^2}{\left(1-a^2\right)\left(1-b^2\right)}-\log_2\left(3a^2+3b^2-1\right)$.\\
		Áp dụng bất đẳng thức AM-GM, ta có $\left(1-a^2\right)\cdot \left(1-b^2\right)\le \dfrac{\left(2-a^2-b^2\right)^2}{4}$.\\
		Suy ra $\log_2\dfrac{a^2+b^2}{\left(1-a^2\right)\left(1-b^2\right)}\ge \log_2\dfrac{4\left(a^2+b^2\right)}{\left(2-a^2-b^2\right)^2}$.\\
		Khi đó
		$P\ge Q$ với $Q=3\left(a^2+b^2\right)^2-4\left(a^2+b^2\right)+\dfrac{4}{3}+\log_2\dfrac{4\left(a^2+b^2\right)}{\left(2-a^2-b^2\right)^2\left(3a^2+3b^2-1\right)}$.\\
		Đặt $t=a^2+b^2\Rightarrow t\in \left(\dfrac{1}{3};2\right)$, suy ra $Q=3t^2-4t+\dfrac{4}{3}+\log_2\dfrac{4t}{(2-t)^2(3t-1)}$.\\
		Ta có $3t^2-4t+\dfrac{4}{3}=3\left(t-\dfrac{2}{3}\right)^2\ge 0$, $\forall t\in \left(\dfrac{1}{3};2\right)$.\\
		Xét $f(t)=\log_2\dfrac{4t}{(2-t)^2(3t-1)}=\log_2(4t)-2\log_2(2-t)-\log_2(3t-1)$ với $t\in \left(\dfrac{1}{3};2\right)$.\\
		Ta có $f'(t)=\dfrac{1}{t}+\dfrac{2}{2-t}-\dfrac{3}{3t-1}=\dfrac{6t^2-t-2}{t(2-t)(3t-1)}$.\\
		Suy ra $f'(t)=0\Leftrightarrow \hoac{& t=\dfrac{2}{3} \\ & t=-\dfrac{1}{2}.}$\\
		Bảng biến thiên của $f(t)$ trên $\left(\dfrac{1}{3};2\right)$.
		\begin{center}
			\begin{tikzpicture}[>=stealth]
			\tkzTabInit[nocadre=false,lgt=1.2,espcl=3,deltacl=0.5]
			{$t$/1 ,$f'(t)$/.7,$f(t)$/2}
			{$\dfrac{1}{3}$ , $\dfrac{2}{3}$ , $2$}
			\tkzTabLine{ , - , $0$ , + , }
			\tkzTabVar{+/, -/$\log_2\dfrac{3}{2}$ , +/}
			\end{tikzpicture}
		\end{center}
		Từ đó suy ra $Q\ge \log_2\dfrac{3}{2}$ và $\min P=\log_2\dfrac{3}{2}$ khi $a=b=\dfrac{1}{\sqrt{3}}$.\\
		Vậy bài toán tương đương với $\log_2\dfrac{3}{2}\ge \log_2(3m)\Leftrightarrow 0<m\le \dfrac{1}{2}$.\\
		Vì $m$ nguyên dương nên không tồn tại $m$ thỏa mãn bài.
	}

%[Thi thử, THPT Nguyễn Huệ, Bà Rịa Vũng Tàu, 2020]%[Hùng Nam Lý, 12EX11]%[2H1G3-3]
	Cho hình chóp $S.ABCD$ có đáy $ABCD$ là hình bình hành. Gọi $M$ là một điểm di động trên cạnh $SC$ và $\dfrac{CM}{SM}=k$ với $k>0$. Mặt phẳng $(\alpha)$ chứa $AM$ và song song với $BD$; $(\alpha)$ cắt $SB$, $SD$ lần lượt tại $N$, $P$. Gọi $V$ là thể tích của khối chóp $S.ABCD$. Khi đó thể tích khối chóp $C.APMN$ lớn nhất bằng
	\choice
	{$\left(10-4\sqrt{2}\right)V$}
	{\True $\left(6-4\sqrt{2}\right)V$}
	{$\left(12-4\sqrt{2}\right)V$}
	{$\left(8-4\sqrt{2}\right)V$}
	\loigiai{
		\immini
		{
			Vì $\dfrac{CM}{SM}=k$ nên $\dfrac{SC}{SM}=1+k$ hay\\ $\dfrac{SM}{SC}=\dfrac{1}{k+1}$.\\
			Gọi $O$ là tâm của $ABCD$ và $I=SO\cap AM$.\\
			Qua $I$ kẻ đường thẳng song song với $BD$, cắt $SB$, $SD$ lần lượt tại $N$, $P$.\\
			Ta có $\dfrac{SB}{SN}=\dfrac{SD}{SP}$ và\\ 
			$\dfrac{SB}{SN}+\dfrac{SD}{SP}=\dfrac{SC}{SM}+\dfrac{SA}{SA}=\dfrac{SM+CM}{SM}+1$
			\\
			$\Rightarrow \dfrac{SB}{SN}+\dfrac{SD}{SP}=k+2$\\
			$\Rightarrow \dfrac{SB}{SN}=\dfrac{k+2}{2}\Rightarrow \dfrac{BN}{SB}=\dfrac{k}{k+2}$.
		}
		{
			\begin{tikzpicture}[scale=.8, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\tikzset{label style/.style={font=\footnotesize}}
			\coordinate (A) at (0,0);
			\coordinate (B) at (-2,-3);
			\coordinate (D) at (5,0);
			\coordinate (C) at ($(B)+(D)-(A)$);
			\coordinate (S) at ($(A)+(-.5,4)$);
			\coordinate (O) at ($(A)!1/2!(C)$);
			\coordinate (M) at ($(S)!2/3!(C)$);
			\tkzInterLL(A,M)(S,O) \tkzGetPoint{I}
			\tkzDefLine[parallel=through I](D,B)\tkzGetPoint{x}
			\tkzInterLL(I,x)(S,D) \tkzGetPoint{P}
			\tkzInterLL(I,x)(S,B) \tkzGetPoint{N}
			\tkzDrawSegments(S,B S,C S,D B,C C,D M,N M,P C,N C,P)
			\tkzDrawSegments[dashed](A,D A,B S,A B,D A,C S,O A,M N,P A,N A,P)
			\tkzDrawPoints[fill=black](S,A,B,C,D,O,M,I,N,P)
			\tkzLabelPoints[above](S)
			\tkzLabelPoints[below](A,B,D,O,I)
			\tkzLabelPoints[right](C,M,P)
			\tkzLabelPoints[left](N)
			\end{tikzpicture}
		}
		\noindent
		Ta có $V_{S.ABMP}=V_{S.ANM}+V_{S.APM}=2V_{S.ANM}$.\\
		Ta có
		\begin{itemize}
			\item $\dfrac{V_{S.ANM}}{V_{S.ABC}}=\dfrac{SN}{SB}\cdot \dfrac{SM}{SC}=\dfrac{2}{k+2}\cdot \dfrac{1}{k+1}\Rightarrow V_{S.ANM}=\dfrac{2}{(k+2)(k+1)}V_{S.ABC}=\dfrac{1}{(k+2)(k+1)}V$\\
			$\Rightarrow V_{S.ABMP}=\dfrac{2}{(k+2)(k+1)}V$.
			\item $V_{P.ACD}=V_{N.ABC}$ và\\
			$V_{N.ABC}=\dfrac{1}{3}\cdot S_{\triangle ABC}\cdot \mathrm{d}(N;(ABC))=\dfrac{1}{3}\cdot \dfrac{1}{2}\cdot S_{ABCD}\cdot \dfrac{BN}{SB}\cdot \mathrm{d}(S;(ABCD))=\dfrac{1}{2}\cdot \dfrac{k}{k+2}\cdot V$.
		\end{itemize}
		Khi đó $V_{C.ANMP}=V-V_{S.ANMP}-V_{N.ABC}-V_{P.ACD}=V-\dfrac{2}{(k+1)(k+2)}V-\dfrac{k}{k+2}V$\\
		$\Rightarrow V_{C.ANMP}=\dfrac{2k}{(k+1)(k+2)}V$.\\
		Xét $f(k)=\dfrac{2k}{(k+1)(k+2)}=\dfrac{2k}{k^2+3k+2}$ với $k>0$.\\
		Ta có $f'(k)=\dfrac{2(k^2+3k+2)-2k(2k+3)}{(k^2+3k+2)^2}=\dfrac{-2k^2+4}{(k^2+3k+2)^2}$.\\
		Suy ra $f'(k)=0\Leftrightarrow k=\sqrt{2}$.\\
		Bảng biến thiên
		\begin{center}
			\begin{tikzpicture}[>=stealth]
			\tkzTabInit[nocadre=false,lgt=1.2,espcl=3,deltacl=0.5]
			{$k$/.7 ,$f'(k)$/.7,$f(k)$/2}
			{$0$ , $\sqrt{2}$ , $+\infty$}
			\tkzTabLine{ , +, $0$ , - , }
			\tkzTabVar{-/$0$ , +/$6-4\sqrt{2}$ , -/$0$}
			\end{tikzpicture}
		\end{center}
		Vậy thể tích khối chóp $C.APMN$ lớn nhất bằng $(6-4\sqrt{2})V$ khi $k=\sqrt{2}$.
	}
